import './Login.css'
import { useNavigate, Link } from "react-router-dom";
import { useState } from 'react'
// import Signup from '../Signup/Signup'
// import About from '../About/About'
// import Navbar from "../Navbar/Navbar"







export default function Login({ setUser, setIsAuthenticated, setUserWelcome }) {    

    // the following is from original /login
    // the following is from original /login
    // the following is from original /login
    // const [userWelcome, setUserWelcome] = useState('')
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [loginError, setLoginError] = useState([])
    const navigate = useNavigate()

    // added from /signup
    // added from /signup
    // added from /signup
    // added from /signup
    const [name, setName] = useState("");
    const [signupErrors, setSignupErrors] = useState([])
    const [passwordConfirmation, setPasswordConfirmation] = useState("");

    // login submit
    // login submit
    // login submit
    const loginOnSubmit = (e) => {
        e.preventDefault()
        const user = {
            username: username,
            password: password
        }
        fetch('/login', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(user)
        })
        .then(res => {
            if(res.ok) {
                res.json()
                .then(user => {
                    setUser(user)
                    setIsAuthenticated(true)
                    // setUserWelcome(user.message)
                    // console.log(user.message)
                    navigate('/about')
                })
            } else {
                    res.json()
                    .then(json => setLoginError(json.error))
                }
            })
        }

    return (
        <>
        <div className="main-horizontal-container">
            <form onSubmit={loginOnSubmit} className="form">
                <div className="container1">
                    <div className="container4">
                        <p></p>
                    <h3 className="login-h3">Login</h3>
                    <Link to='/signup'><h3 className="login-h3">Signup</h3></Link>

                    </div>
                    <input 
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Username" 
                        name="uname" 
                        />
                    <input 
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Password" 
                        name="pwd" 
                        />
                    <div className="container2">
                        <button className="button1" type="submit">Login</button>
                    </div>
                </div>
                <div className="container3">
                    {loginError ? <div>{loginError}</div> : null}
                </div>
            </form>
        </div>
        </>
    );
}
