import '../App.css';
import { React, useEffect, useState } from 'react'
import { Routes, Route } from "react-router-dom";
import Login from "./Login/Login"
import Signup from "./Signup/Signup"
import Navbar from "./Navbar/Navbar"
import Home from './Home/Home'
import About from './About/About'

function App() {

  const [isAuthenticated, setIsAuthenticated] = useState(false)
  const [user, setUser] = useState(null)
  const [userWelcome, setUserWelcome] = useState('')



  // console.log(user)
  // console.log(isAuthenticated)

  useEffect(() => {
    fetch('/authorized_user')
    .then(res => {
        if (res.ok) {
          res.json()
          .then(user => {
              setIsAuthenticated(true)
              setUser(user)
          })
        }
    })
}, [])



if (!isAuthenticated) return <Login error={"Please Login"} setUser={setUser} setIsAuthenticated={setUser} />

return (

    <div className="container">
      <Navbar />
      <Routes>
        <Route path="/signup" element={<Signup setUser={setUser} setIsAuthenticated={setUser} />} />
        <Route path="/login" element={<Login setUser={setUser} setIsAuthenticated={setIsAuthenticated} />} />
        <Route path="/about" element={<About />} />
        <Route path="/home" element={<Home />} />
        <Route path="/" element={<Home />} />
      </Routes>
    </div>

  );
}

export default App;
